import React, { Component } from 'react';
import { Card, CardImg, CardTitle, CardBody, CardText, BreadcrumbItem, Breadcrumb } from 'reactstrap';

class FashionInfo extends Component {

    renderSelectedFashion(fashion) {
            return (
                <div className="col-md-3">
                <Card>
                    <CardImg top src={fashion.image} alt={fashion.name}/>
                    <CardBody>
                        <CardTitle>{fashion.name}</CardTitle>
                        <CardText>{fashion.description}</CardText>
                    </CardBody>
                </Card>
                </div>
            )
        }

        renderComments({fashions}) {
            if (fashions){
                return (
                    <div className="col-md-5">
                        <h4>fashions</h4>
                        { fashions.map(fashion => {
                            return (
                                <div key={fashion.id}>
                                    <p>{fashion.text}</p>
                                    <p>{fashion.author},   
                                    {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'}).format(new Date(Date.parse(fashion.date)))}
                                    </p>
                                </div>
                            );
                        })}
                    </div> 
                    );
                }
             return <div />;
        }    


    render() {
        if (this.props.fashion) {
            return (
               // <div className="container">
                    <div className="row">
                        {this.renderFashion(this.props.fashion)}
                        {this.renderFashions(this.props.fashion.fashions)}
                    </div>

            );
        }
        return <div />;
    }
    }

export default FashionInfo;
