import React, { Component } from 'react';
import { Navbar, NavbarBrand } from 'reactstrap';
import Boutique from './BoutiqueComponent';
import { FASHIONS } from '../shared/fashions';

class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fashions: FASHIONS,
            selectedCampsite: null
        };
    }

    onFashionSelect(fashionId) {
        this.setState({selectedFshion: fashionId});
    }

    render() {
        return (
            <div>
                <Navbar dark color="primary">
                    <div className="container">
                        <NavbarBrand href="/">Mode Boutique</NavbarBrand>
                    </div>
                </Navbar>
                <Boutique fashions={this.state.fashions} onClick={fashionId => this.onFashionSelect(fashionId)}/>
                <FashionInfo campsite={this.state.campsites.filter(campsite => campsite.id === this.state.selectedCampsite)[0]} />               

            </div>
        );
    };
}

export default Main;
