import React, { Component } from 'react';
import Boutique from './BoutiqueComponent';
import FashionInfo from './FashionInfoComponent';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import Home from './HomeComponent';
import { Switch, Route, Redirect } from 'react-router-dom';
import { FASHIONS } from '../shared/fashions';

class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fashions: FASHIONS,

        };
    }

    onFashionSelect(fashionId) {
        this.setState({selectedFashion: fashionId});
    }

    render() {
        return (
            <div>
                <Header />
                <Boutique fashions={this.state.fashions} onClick={fashionId => this.onFashionSelect(fashionId)}/>
                <FashionInfo fashion={this.state.fashions.filter(fashion => fashion.id === this.state.selectedFashion)[0]} />               
                <Footer />
            </div>
        );
    };
}

export default Main;
