import React from 'react';
import { Card, CardImg, CardTitle, CardBody, CardText, BreadcrumbItem, Breadcrumb } from 'reactstrap';

class FashionInfo extends comment {

    renderSelectedFashion(fashion) {
            return (
                <div className="col-md-3">
                <Card>
                    <CardImg top src={fashion.image} alt={fashion.name}/>
                    <CardBody>
                        <CardTitle>{fashion.name}</CardTitle>
                        <CardText>{fashion.description}</CardText>
                    </CardBody>
                </Card>
                </div>
            )
        }




    render() {
        if (this.props.fashion) {
            return (
                <div className="container">
                    <div className="row">
                        {this.renderFashion(this.props.fashion)}
                        {this.renderFashions(this.props.fashion.comments)}
                    </div>
                </div>
            );
        }
        return <div />;
    }
    }

export default FashionInfo;
