import React from 'react';
import { Card, CardImg, CardImgOverlay, CardTitle } from 'reactstrap';

function RenderBoutiqueItem({fashion, onClick}) {
    return(
        <Card onClick={() => this.props.onClick(fashion.id)}>
            <CardImg width="100%" src={fashion.image} alt={fashion.name} />
                <CardImgOverlay>
                    <CardTitle>{fashion.name}</CardTitle>
                </CardImgOverlay>    
                    </Card> 
    );
}

function Boutique(props) {
        const boutique = props.fashions.map(fashion => {
            return (
                <div key={fashion.id} className="col-md-3">
                    <RenderBoutiqueItem fashion={fashion} onClick={props.onClick} />
                </div>
            );
        });

        return (
            <div className="container">
                <div className="row">
                    {boutique}
                </div>

            </div>
        );
    }

export default Boutique;
