import React, { Component } from 'react';
import FashionInfo from './FashionInfoComponent';
import { Card, CardImg, CardImgOverlay, CardText, CardBody, CardTitle } from 'reactstrap';

class Boutique extends Component {

    renderSelectedFashion(fashion) {
        if (fashion) {
            return (
                <Card>
                    <CardImg top src={fashion.image} alt={fashion.name}/>
                    <CardBody>
                        <CardTitle>{fashion.name}</CardTitle>
                        <CardText>{fashion.description}</CardText>
                    </CardBody>
                </Card>
            );
        }
        return <div />;
    }

    render() {
        const boutique = this.props.fashions.map(fashion => {
            return (
                <div key={fashion.id} className="col-md-3">
                   <Card onClick={() => this.props.onClick(fashion.id)}>
                        <CardImg width="100%" src={fashion.image} alt={fashion.name} />
                        <CardImgOverlay>
                            <CardTitle>{fashion.name}</CardTitle>
                        </CardImgOverlay>    
                    </Card>  
                </div>
            );
        });

        return (
            <div className="container">
                <div className="row">
                    {boutique}
                </div>
                <div className="row">
                    <div className="col-md-3">
                        {this.renderSelectedFashion(this.state.selectedFshion)}
                    </div>
                </div>
            </div>
        );
    }
}

export default Boutique;
